import { Outlet, Navigate, Route, Routes, useLocation } from 
'react-router-dom';
import { useSelector } from 'react-redux';
import { Home, Login, Profile, Register, Landing} from './pages';
import { BrowserRouter, useRoutes } from 'react-router-dom';


function Layout(){
  const { user } = useSelector(state => state.user);
  const location = useLocation();
  return user?.token ? (  
    <Navigate to='/home' />
  ) : (
    <Navigate to='/landing' state={{from: location}} replace />
  );
}

function App() {
    const {theme} = useSelector((state) => state.theme);

  return (
    <div data-theme={theme} className=" App w-full min-h-[100vh]"> 
            <div className="blur" style={{top: '-18%', right: '0'}}></div>
            <div className="blur" style={{top: '36%', left: '-8rem'}}></div>
      <Routes>
          <Route element={<Layout />}>
            <Route path='/profile/:id?' element={<Profile />} />
            <Route path='/home' element={<Home />} />
          </Route>
          <Route path='/landing' element={<Landing />} />
          <Route path='/register' element={<Register />} />
          <Route path='/login' element={<Login />} />
      </Routes>
    </div>


    // const routes = useRoutes([
    //   { path: '/profile/:id', element: <Profile /> },
    //   { path: '/home', element: <Home /> },
    //   { path: '/register', element: <Register /> },
    //   { path: '/login', element: <Login /> },
    //   { path: '/', element: <Login /> },
    // ]);


  );
}



export default App;
