import React, { useState } from 'react'
import axios from 'axios'

      const Register = () => {
          const [formData, setFormData] = useState({
              name: '',
              phoneNumber: '',
              collegeName: '',
          });

          const handleChange = (e) => {
              setFormData({ ...formData, [e.target.name]: e.target.value });
          };

          const handleSubmit = async (e) => {
              e.preventDefault();
              try {
                  const response = await axios.post('http://localhost:5000/register', formData);
                  console.log(response.data);
                  window.location.href = '/home'; // redirect to home page
              } catch (error) {
                  console.log(error);
              }
          };

          return (
              <div class='flex items-center justify-center min-h-screen font-ClashDisplay-Medium'>
                <div class='max-w-md mx-auto '>
                  <h1> <a href="/"> SNEAK IN </a> </h1>
                  <br />
                  <br />
                    <h2>Register</h2>
                  <br />
                  <br />
                  <form onSubmit={handleSubmit}>

                      <label>Name:</label><br />
                      <input type="text" name="name" placeholder='abc xyz' value={formData.name} onChange={handleChange} required />
                      <br />

                      <label>College Name:</label> <br />
                      <input type="text" name="collegeName" placeholder='Sathaye College' value={formData.collegeName} onChange={handleChange} required />
                      <br />

                      <label>Class:</label> <br />
                      <input type="text" name="class" placeholder='TYBA' value={formData.class} onChange={handleChange} required />
                      <br />
                      <br />

                      <button  type="submit" > SUBMIT </button>
                  </form>
                  </div>
              </div>
          );
      };

export default Register;
